argsString:
{
  "_id": "goal_state",
  "tags": [
    "df"
  ],
  "props": {},
  "content": "A \\emph{goal-state} is a state of an agent which specifies an outcome and which is, or could be, related to the agent’s actions in such a way that these actions are directed to the outcome represented.\n% ",
  "source_file_info": {
    "path": "/Users/stephenbutterfill/Documents/writing/joint action julian kiverstein routledge handbook of the social mind",
    "basename": "joint_action.tex",
    "ext": ".tex",
    "modified": "2015-08-17T15:12:43.000Z"
  }
}

